import React, { useState, useEffect } from "react";
import { app } from "./FirebaseConfig";
import { signOut, getAuth, onAuthStateChanged } from 'firebase/auth'
import { useNavigate } from 'react-router-dom'
import { getFirestore, doc, getDoc, getDocs, collection } from 'firebase/firestore';
import { singer } from "../assets/instruments/singer.jpg"
import { drums } from "../assets/instruments/drums.jpg"
import { guitar } from "../assets/instruments/guitar.jpg"
import { bass } from "../assets/instruments/bass.jpg"
import { piano } from "../assets/instruments/piano.jpg"

function Home(){
    const [allMusiciens, setAllMusiciens] = useState([]);
    const [userNameConnected, setUserNameConnected] = useState();
    const [userUidConnected, setUserUidConnected] = useState();

    const database = getAuth(app);
    const history = useNavigate()

    const handleClick = () => {
        signOut(database).then(val => {

            history('/')
        })
    }

    let instruments = {
        1: 'drums',
        2: 'guitar',
        3: 'bass',
        4: 'piano',
        5: 'singer',
    };


    useEffect(() => {
        const auth = getAuth();
        const db = getFirestore()

        onAuthStateChanged(auth, async (user) => {
            if (user) {
                const username = user.displayName;
                setUserNameConnected(username);
                setUserUidConnected(user.uid);
                console.log('Username de l\'utilisateur connecté:', username);
                console.log('UserUid de l\'utilisateur connecté:', user.uid);

                console.log(instruments[1]);
                try {
                    const usersCollection = collection(db, 'user');
                    const usersSnapshot = await getDocs(usersCollection);
                    const usersData = [];
                    usersSnapshot.forEach((doc) => {
                        if (doc.id !== user.uid)
                            usersData.push({ id: doc.id, ...doc.data() });
                    });
                    setAllMusiciens(usersData);
                    console.log(usersData);
                } catch (error) {
                    console.error('Erreur lors de la récupération des données des utilisateurs:', error.message);
                }

            } else {
                console.log('Utilisateur déconnecté');
            }
        });
    }, []);

    function getImage(instrument) {
        return "../assets/instruments/${instruments[data.instrument]}.jpg"
    }

    return (
        <div class="bg-gray-200/50">
            <section class="max-w-6xl mx-auto px-4 sm:px-6 lg:px-4 py-12">
                <div class="text-center pb-12">
                    <h2 class="text-base font-bold text-indigo-600">
                        Trouve tes futurs partenaire de soul
                    </h2>
                    <h1 class="font-bold text-3xl md:text-4xl lg:text-5xl font-heading text-gray-900">
                        Construis le groupe de tes reves
                    </h1>
                </div>
                <div class="flex flex-col space-y-8">

                    {
                        allMusiciens.map((data) => (
                            <div class="w-full bg-white rounded-lg p-12 flex flex-col justify-center items-center">
                                <div class="mb-8">
                                    <img class="object-center object-cover rounded-full h-36 w-36" src={getImage(data.instrument)} alt="photo" />
                                </div>
                                <div class="text-center">
                                    <p class="text-xl text-gray-700 font-bold mb-2">{data.niveau}</p>
                                    <p class="text-xl text-gray-700 font-bold mb-2">Dany Bailey</p>
                                    <p class="text-base text-gray-400 font-normal">Software Engineer</p>
                                </div>
                            </div>
                        ))
                    }

                </div>
            </section>
            <button onClick={handleClick}>Sign Out</button>
        </div>
    )
}

export default Home;
