import React, { useState } from 'react';
import { database } from "./FirebaseConfig";
import { createUserWithEmailAndPassword } from 'firebase/auth'
import coverPicture from "../assets/meet-your-soul-pic-2.jpg";
import { useNavigate } from 'react-router-dom'

function RegisterAndLogin(){
    const [login, setLogin] = userState(false);
    const [alertMessage, setAlertMessage] = useState(null);

    const history = useNavigate()

    const SignIn = (e) => {
        e.preventDefault()
        const email = e.target.email.value;
        const password = e.target.password.value;

        createUserWithEmailAndPassword(database, email, password).then(data => {
            console.log(data, "authData");
            history('/home')
        }).catch((error) => {
            const errorCode = error.code;
            const errorMessage = error.message;
            setAlertMessage(`${errorMessage}`);
        })
    }

    return (
        <div>
            {/* Affichage de l'alerte */}
      {alertMessage && (
        <div className="fixed inset-0 z-50 flex items-center justify-center overflow-x-hidden overflow-y-auto outline-none focus:outline-none">
          <div className="relative w-auto max-w-md mx-auto my-6">
            {/* Contenu de l'alerte */}
            <div className="relative flex flex-col w-full bg-white border-0 rounded-lg shadow-lg outline-none focus:outline-none">
              {/* En-tête de l'alerte */}
              <div className="flex items-start justify-between p-5 border-b border-solid rounded-t">
                <h3 className="text-xl font-semibold text-red-500">
                  Erreur dans la création de l'utilisateur
                </h3>
                <button
                  onClick={() => setAlertMessage(null)}
                  className="p-1 ml-auto -mt-1 -mr-2 text-black bg-transparent border-0 text-3xl leading-none font-semibold outline-none focus:outline-none"
                >
                  <span className="text-black bg-transparent h-6 w-6 text-2xl block outline-none focus:outline-none">×</span>
                </button>
              </div>
              {/* Contenu de l'alerte */}
              <div className="relative p-6 flex-auto">
                <p className="my-4 text-gray-600 text-lg leading-relaxed">
                  {alertMessage}
                </p>
              </div>
            </div>
          </div>
        </div>
      )}
            <div class="flex h-screen w-full items-center justify-center bg-gray-900 bg-cover bg-no-repeat" style={{backgroundImage: `url(${coverPicture})`}}>
                <div class="rounded-xl bg-gray-800 bg-opacity-50 px-16 py-10 shadow-lg backdrop-blur-md max-sm:px-8">
                    <div class="text-white">
                        <div class="mb-8 flex flex-col items-center">
                            <h1 class="mb-2 text-4xl">Meet your soul</h1>
                        </div>
                    <form onSubmit={(e) => SignIn(e, login ? 'signin' : 'signup')}>
                        <div class="mb-4 text-lg">
                            <input class="rounded-3xl border-none bg-[#112550] bg-opacity-50 px-6 py-2 text-center text-inherit placeholder-gray-500 shadow-lg outline-none backdrop-blur-md" type="email" name="email" placeholder="Email" />
                        </div>
                        <div class="mb-4 text-lg">
                            <input class="rounded-3xl border-none bg-[#112550] bg-opacity-50 px-6 py-2 text-center text-inherit placeholder-gray-500 shadow-lg outline-none backdrop-blur-md" type="Password" name="password" placeholder="Password" />
                        </div>
                        <div class="mt-8 flex justify-center text-lg text-black">
                            <button type="submit" class="rounded-3xl bg-[#f1f1f1e8] bg-opacity-50 px-10 py-2 text-black shadow-xl backdrop-blur-md transition-colors duration-300 hover:bg-[#ffffff]">{Register}</button>
                        </div>
                    </form>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default RegisterAndLogin;
