import React from "react";
import { database } from "./FirebaseConfig";
import coverPicture from "../assets/meet-your-soul-pic-2.jpg";

function RegisterAndLogin(){

    const Login = (e) => {
        e.preventDefault()
        console.log(e.target.email)
    }

    return (
        <div class="flex h-screen w-full items-center justify-center bg-gray-900 bg-cover bg-no-repeat" style={{backgroundImage: `url(${coverPicture})`}}>
            <div class="rounded-xl bg-gray-800 bg-opacity-50 px-16 py-10 shadow-lg backdrop-blur-md max-sm:px-8">
                <div class="text-white">
                    <div class="mb-8 flex flex-col items-center">
                        <h1 class="mb-2 text-4xl">Meet your soul</h1>
                    </div>
                <form action="#">
                    <div class="mb-4 text-lg">
                        <input class="rounded-3xl border-none bg-[#112550] bg-opacity-50 px-6 py-2 text-center text-inherit placeholder-gray-500 shadow-lg outline-none backdrop-blur-md" type="email" name="name" placeholder="Email" />
                    </div>
                    <div class="mb-4 text-lg">
                        <input class="rounded-3xl border-none bg-[#112550] bg-opacity-50 px-6 py-2 text-center text-inherit placeholder-gray-500 shadow-lg outline-none backdrop-blur-md" type="Password" name="password" placeholder="Password" />
                    </div>
                    <div class="mt-8 flex justify-center text-lg text-black">
                        <button type="submit" class="rounded-3xl bg-[#f1f1f1e8] bg-opacity-50 px-10 py-2 text-black shadow-xl backdrop-blur-md transition-colors duration-300 hover:bg-[#ffffff]">Login</button>
                    </div>
                </form>
                </div>
            </div>
        </div>
    )
}

export default RegisterAndLogin;
