import React, { useState } from "react";
import { app } from "./FirebaseConfig";
import { signOut, getAuth, onAuthStateChanged } from 'firebase/auth'
import { useNavigate } from 'react-router-dom'
import { getFirestore, doc, getDoc, collection } from 'firebase/firestore';

function Home(){
    const history = useNavigate()
    const auth = getAuth();
    const database = getAuth(app);
    const db = getFirestore()

    const [allMusiciens, setAllMusiciens] = useState([]);
    const [userNameConnected, setUserNameConnected] = useState();
    const [userUidConnected, setUserUidConnected] = useState();


    const handleClick = () => {
        signOut(database).then(val => {

            history('/')
        })
    }

    onAuthStateChanged(auth, async (user) => {
        if (user) {
            try {
                const usersCollection = collection(db, 'user');
                const usersSnapshot = await getDocs(usersCollection);
                const usersData = [];
                usersSnapshot.forEach((doc) => {
                  usersData.push({ id: doc.id, ...doc.data() });
                });
                setUsers(usersData);
                console.log(usersData)
            } catch (error) {
                console.error('Erreur lors de la récupération des données des utilisateurs:', error.message);
            }
            const username = user.displayName;
            setUserNameConnected(username);
            setUserUidConnected(user.uid);
            console.log('Username de l\'utilisateur connecté:', userNameConnected);
            console.log('UserUid de l\'utilisateur connecté:', userUidConnected);

            const userDocRef = doc(db, 'user', user.uid);
            const userDocSnap = await getDoc(userDocRef);

        } else {
            console.log('Utilisateur déconnecté');
        }
    });

    return (
        <div>
            <h1>Home</h1>
            <button onClick={handleClick}>Sign Out</button>
        </div>
    )
}

export default Home;
